table.sql:
CREATE TABLE IF NOT EXISTS `combo_reasons` (
  `acmprid` int(11) DEFAULT NULL,
  `fieldname` varchar(20) DEFAULT NULL,
  `reason` text
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

CREATE TABLE IF NOT EXISTS `vtigress_acmpr_griddetails` (
  `acmprid` int(11) NOT NULL,
  `activities` text,
  `substance` text,
  `accountname` text
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE IF NOT EXISTS `vtigress_measurement` (
  `fieldname` varchar(100) DEFAULT NULL,
  `measurement` varchar(200) DEFAULT NULL,
  UNIQUE KEY `fieldname` (`fieldname`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;
